mapper.select:

select idx, hit_cnt, crea_id, crea_dtm, title, contents
from tb_board
where idx=#{IDX}

mapper.update:

update tb_board
set hit_Cnt = nvl(hit_cnt, 0) + 1
where idx = #{IDX}


update tb_board
set title=#{TITLE}, 
contents = #{CONTENTS}

where idx = #{IDX}


update tb_board
set del_gb = 'Y'
where idx = #{IDX}
